import React, { useCallback } from "react";

import "./styles.css";

import { show_password, hide_password } from "../../../assets/index";

const PersonalForm = React.forwardRef(
  ({ error, errorMessage, section, setVisible, isVisible, ...props }, ref) => {
    const togglePassword = useCallback(() => {
      setVisible();
    }, [isVisible]);

    return (
      <div className="data-form">
        <h3>{section}</h3>
        <input ref={ref} {...props} />
        {error && <p className="message">{errorMessage}</p>}
        {setVisible && (
          <button>
            <img src={isVisible ? hide_password : show_password} />
          </button>
        )}
      </div>
    );
  }
);

export default PersonalForm;
