import React from "react";
import { useSelector } from "react-redux";

import { InfoBlock } from "../../components/ui/index";

import { pencil } from "../../assets/index";

import "./styles.css";
import SideNav from "../../components/routes/sideNav";

const Account = () => {
  const { fullName, email, avatarUrl } = useSelector(
    (state) => state.auth.data
  );

  const shippingStyles = {
    height: 50 + "vh",
    margin: 5 + "%",
  };

  const passwordStyles = {
    height: 2 + "vh",
    margin: 5 + "%",
  };

  const personalData = [fullName, email];
  const addressData = [];
  const password = ["********"];

  return (
    <div className="account-container">
      <SideNav avatar={avatarUrl} />
      <div className="middle">
        <InfoBlock data={personalData} section={"Presonal Data"} />
        <InfoBlock section={"My shipping addresses"} styles={shippingStyles}>
          <button>Add new address</button>
        </InfoBlock>
        <InfoBlock
          section={"Change Password"}
          data={password}
          styles={passwordStyles}
        />
      </div>
      <div className="right">
        <div className="def-shipping">
          <h1>Default Shipping</h1>
          <div>None</div>
        </div>
        <div className="shipping-method">
          <h1>Default Shipping Method</h1>
          <div>None</div>
        </div>
      </div>
    </div>
  );
};

export default Account;
