import React, { useCallback } from "react";
import PersonalForm from "./PersonalForm";
import { useForm } from "react-hook-form";
import { useSelector } from "react-redux";
import { yupResolver } from "@hookform/resolvers/yup";
import { editDataSchema } from "../../../schemas";

const PersDataWrapper = ({ setVisible }) => {
  const { fullName, email } = useSelector((state) => state.auth.data);

  const {
    register,
    handleSubmit,
    formState: { errors, isValid },
  } = useForm({
    defaultValues: {
      fullName: fullName,
      email: email,
      birthday: "2023-01-01",
    },
    resolver: yupResolver(editDataSchema),
    mode: "onChange",
  });

  const onClickCancel = useCallback(() => {
    setVisible(true);
  }, [setVisible]);

  const onSubmit = () => {};

  return (
    <form onSubmit={handleSubmit(onSubmit)} className="data-wrapper">
      <PersonalForm
        {...register("fullName")}
        type={"text"}
        section={"Full Name"}
      />
      <PersonalForm {...register("email")} type={"text"} section={"Email"} />
      <PersonalForm
        {...register("birthday")}
        type={"date"}
        section={"Birthday"}
      />
      <button onClick={onClickCancel}>Cancel</button>
      <button disabled={register.defaultValues} type="submit">
        Save
      </button>
    </form>
  );
};

export default PersDataWrapper;
