import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import "./styles.css";

import { fetchCards } from "../../redux/slices/cards";
import { getPageName } from "../../helpers/PageName";

import { Card, CardSkeleton, FilterInput } from "../../components/ui/index";

export const Cards = () => {
  const currentPage = getPageName();

  const dispatch = useDispatch();
  const { cards } = useSelector((state) => state.cards);

  const isCardsLoading = cards.status === "loading";

  const [searchResult, setSearchResult] = useState("");

  useEffect(() => {
    dispatch(fetchCards());
  }, [dispatch]);

  return (
    <div className="humans-container">
      <div className="head">
        <p>{currentPage}`s sneakers</p>
        <FilterInput
          placeholder={"SEARCH"}
          label={"SEARCH"}
          type={"text"}
          searchResult={searchResult}
          setSearchResults={setSearchResult}
        />
      </div>
      <div className="cards">
        {isCardsLoading ? (
          <CardSkeleton cards={4} />
        ) : (
          cards.items
            .filter((sneaker) => {
              const searchTerm = searchResult.toLowerCase();
              const model = sneaker.model.toLowerCase();
              const brand = sneaker.brand.toLowerCase();

              return brand.includes(searchTerm.includes(model));
            })
            .map((sneaker) => {
              return sneaker.sex === currentPage ? (
                <Card card={sneaker} />
              ) : currentPage !== "Kids" &&
                sneaker.sex === "Unisex" &&
                !sneaker.forKids ? (
                <Card card={sneaker} />
              ) : (
                currentPage === "Kids" &&
                sneaker.forKids && <Card card={sneaker} />
              );
            })
        )}
      </div>
    </div>
  );
};

export default Cards;
