import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import "./styles.css";

import axios from "../../axios";
import { selectIsAuth } from "../../redux/slices/auth";

import { CardSkeleton, CustomLink } from "../../components/ui/index";
import { Card } from "../../components/ui/CardsComponents/index";

import { icon_back } from "../../assets/index";

const Favorites = () => {
  const isAuth = useSelector(selectIsAuth);
  const userData = useSelector((state) => state.auth.data);
  const [cards, setCards] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    if (isAuth) {
      axios.get(`/favorites:${userData?._id}`).then((res) => {
        setCards(res.data.cards);
        setIsLoading(false);
      });
    }
  }, []);

  return (
    <>
      {isAuth ? (
        <div className="favorites-contaner">
          {isLoading ? (
            <CardSkeleton cards={4} />
          ) : (
            cards.map((card) => <Card key={card._id} card={card} />)
          )}
        </div>
      ) : (
        <div className="login">
          <CustomLink to={"/login"}>At first you need to login</CustomLink>
          <img src={icon_back} />
        </div>
      )}
    </>
  );
};

export default Favorites;
