import fromUnixTime from "date-fns/fromUnixTime";

export const getBirthdayDate = (date) => {
  const day = fromUnixTime(date);
  const month = fromUnixTime(date).getUTCMonth();
  const year = fromUnixTime(date).getUTCFullYear();

  return day + "." + month + "." + year;
};
