import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import "./styles.css";

import { fetchCards } from "../../redux/slices/cards";
import { getPageName } from "../../helpers/PageName";

import { Card, CardSkeleton, FilterInput } from "../../components/ui/index";

export const Cards = () => {
  const currentPage = getPageName();

  const dispatch = useDispatch();
  const { cards } = useSelector((state) => state.cards);

  const isCardsLoading = cards.status === "loading";

  const [searchResult, setSearchResult] = useState(cards.items);

  const result = searchResult.map((card) => <Card card={card} />);

  const content = result?.length ? (
    result
  ) : (
    <article>
      <p>No Matching Products</p>
    </article>
  );

  useEffect(() => {
    dispatch(fetchCards());
  }, [dispatch]);

  return (
    <div className="humans-container">
      <div className="head">
        <p>{currentPage}`s sneakers</p>
        <FilterInput
          placeholder={"SEARCH"}
          label={"SEARCH"}
          type={"text"}
          cards={cards}
          setSearchResults={setSearchResult}
        />
      </div>
      <div className="cards">
        {isCardsLoading ? <CardSkeleton cards={4} /> : content}
      </div>
    </div>
  );
};

export default Cards;
