import React, { useReducer } from "react";
import { togglePasswordReducer } from "../../../helpers/PasswordReducer";
import UserPassword from "./UserPassword";

const UserPasswordWrapper = ({ setVisible }) => {
  const [state, dispatch] = useReducer(togglePasswordReducer, {
    old: false,
    new: false,
  });

  console.log(`old ${state.old}, new ${state.new}`);

  return (
    <form className="password-form">
      <h3>New password</h3>
      <UserPassword
        isVisible={state.old}
        setVisible={() => dispatch({ type: "old" })}
        type={state.old ? "text" : "password"}
      />
      <h3>Old password</h3>
      <UserPassword
        isVisible={state.new}
        setVisible={() => dispatch({ type: "new" })}
        type={state.new ? "text" : "password"}
      />
      <div className="btns">
        <button type="button" onClick={setVisible}>
          Cancel
        </button>

        <button type="submit">Submit</button>
      </div>
    </form>
  );
};

export default UserPasswordWrapper;
