import React from "react";
import PersonalForm from "./PersonalForm";
import { useForm } from "react-hook-form";

const PersDataWrapper = () => {
  const {
    register,
    handleSubmit,
    formState: { errors, isValid },
  } = useForm({
    defaultValues: {
      fullName: "",
      email: "",
      birthday: "2023-01-01",
    },
  });

  return (
    <form className="data-wrapper">
      <PersonalForm
        {...register("fullName")}
        type={"text"}
        section={"Full Name"}
      />
      <PersonalForm {...register("email")} type={"text"} section={"Email"} />
      <PersonalForm
        {...register("birthday")}
        type={"date"}
        section={"Birthday"}
      />

      <button type="submit"></button>
    </form>
  );
};

export default PersDataWrapper;
