import React from "react";
import { useDispatch, useSelector } from "react-redux";

import { logout } from "../../redux/slices/auth";

import { CustomLink } from "../../components/ui/index";

import {
  account,
  account_active,
  sneaker,
  sneaker_active,
  exit,
} from "../../assets/index";

import "./styles.css";

const Account = () => {
  const { fullName, email, avatarUrl } = useSelector(
    (state) => state.auth.data
  );

  const dispatch = useDispatch();

  const onClickLogout = () => {
    if (window.confirm("Are you sure you want to log out?")) {
      dispatch(logout());
      window.localStorage.removeItem("token");
    }
  };

  return (
    <div className="account-container">
      <div className="left">
        <div className="links">
          <CustomLink className="account">
            <img src={account} />
            My Account
          </CustomLink>
          <CustomLink className="orders">
            <img src={sneaker} />
            My Orders
          </CustomLink>
          <span />
          <button className="logout" onClick={onClickLogout}>
            <img src={exit} />
            Logout
          </button>
        </div>
      </div>
      <div className="middle">
        <div className="personal-info">
          <h1>Personal Data</h1>
          <div className="data">
            <img />
            <ul>
              <li>{fullName}</li>
              <li>{email}</li>
              <li>28.09.2001</li>
            </ul>
          </div>
        </div>
        <div className="shipping-info">
          <h1>My shipping addresses</h1>
          <div className="ship-list"></div>
          <button>Add new address</button>
        </div>
        <div className="password">
          <h1>Change Password</h1>
        </div>
      </div>
      <div className="right">
        <div className="def-shipping">
          <h1>Default Shipping</h1>
        </div>
        <div className="shipping-method">
          <h1>Default Shipping Method</h1>
        </div>
      </div>
    </div>
  );
};

export default Account;
