import React from "react";

import "./styles.css";

const FilterInput = ({ placeholder, label, type, cards, setSearchResults }) => {
  const handleSearchChange = (e) => {
    if (!e.target.value) return setSearchResults(cards);

    const resultArray = cards.filter(
      (card) =>
        card.includes(e.target.value) || card.body.includes(e.target.value)
    );

    setSearchResults(resultArray);
  };

  return (
    <div className="form__group">
      <input
        onChange={handleSearchChange}
        type={type}
        className="form__field"
        placeholder={placeholder}
      />
      <label className="form__label">{label}</label>
    </div>
  );
};

export default FilterInput;
