import React, { useState } from "react";

import { toparrow, bottomarrow } from "../../../../assets/index";

const Carousel = ({ imageList }) => {
  const [current, setCurrent] = useState(imageList[0]);

  const goToPrevImg = () => {
    const listLength = imageList.length - 1;

    if (current === imageList[0]) {
      setCurrent(imageList[listLength]);
    } else {
      const currentImg = imageList.indexOf(current);
      setCurrent(imageList[currentImg - 1]);
    }
  };

  const goToNextImg = () => {
    const listLength = imageList.length - 1;

    if (current === imageList[listLength]) {
      setCurrent(imageList[0]);
    } else {
      const currentImg = imageList.indexOf(current);
      setCurrent(imageList[currentImg + 1]);
    }
  };

  const pickImg = (v) => {
    setCurrent(v);
  };

  return (
    <div className="mat-images">
      <div className="swiper-container">
        <button onClick={goToPrevImg} className="prev">
          <img alt="top" src={toparrow} />
        </button>
        <div className="swiper-wrapper">
          {imageList.map((v, index) => {
            return (
              <button key={index} onClick={() => pickImg(v)}>
                <img
                  alt=""
                  src={v}
                  style={{
                    border: v === current && "1px solid #44444c",
                  }}
                />
              </button>
            );
          })}
        </div>
        <button onClick={goToNextImg} className="next">
          <img alt="bottom" src={bottomarrow} />
        </button>
      </div>
      <div className="clone-image">
        <img alt="clone" src={current} />
      </div>
    </div>
  );
};

export default Carousel;
