import React, { useState } from "react";

import { heart, filled_heart } from "../../../assets/index";
import { useSelector } from "react-redux";

import axios from "../../../axios";

const CardInfo = ({
  id,
  brand,
  model,
  cost,
  materials,
  technology,
  generalHeight,
  soleHeight,
  fastener,
  color,
  sex,
}) => {
  const [isFocused, setIsFocused] = useState(false);
  const { _id, favorites } = useSelector((state) => state.auth.data);

  const handleFavorites = () => {
    if (favorites.includes(id)) {
      console.log("deleted");
      axios.delete(`/favorites:${_id}`, { cardId: id });
      setIsFocused(!isFocused);
    } else {
      console.log("added");
      axios.post(`/favorites:${_id}`, { cardId: id });
      setIsFocused(!isFocused);
    }
  };

  return (
    <div className="right">
      <div className="name">
        <h1>{model}</h1>
        <h2>{brand}</h2>
      </div>
      <div className="price-line">
        <h1>{cost}</h1>
        <h2>$</h2>
        <button className="heart">
          <img
            alt="heart"
            onClick={handleFavorites}
            src={
              favorites && favorites.find((s) => s === id)
                ? filled_heart
                : heart
            }
          />
        </button>
        <button className="buy">Buy</button>
      </div>
      <div className="description">
        <h2>Materials :</h2>
        <ul>
          <li>{materials}</li>
        </ul>
        <h2>Sex :</h2>
        <ul>
          <li>{sex}</li>
        </ul>
        <h2>Color :</h2>
        <ul>
          <li>{color}</li>
        </ul>
        <h2>Fastener :</h2>
        <ul>
          <li>{fastener}</li>
        </ul>
        <h2>Technology :</h2>
        <ul>
          <li>{technology}</li>
        </ul>
        <h2>Height :</h2>
        <ul>
          <li>General heigth: {generalHeight}</li>
          <li>Sole heigth: {soleHeight}</li>
        </ul>
      </div>
    </div>
  );
};

export default CardInfo;
