import React, { useCallback } from "react";
import PersonalForm from "./PersonalForm";
import { useForm } from "react-hook-form";
import { useSelector } from "react-redux";

const PersDataWrapper = ({ setVisible }) => {
  const { fullName, email } = useSelector((state) => state.auth.data);

  const {
    register,
    handleSubmit,
    formState: { errors, isValid },
  } = useForm({
    defaultValues: {
      fullName: fullName,
      email: email,
      birthday: "2023-01-01",
    },
  });

  onClickCancel = useCallback(() => {
    setVisible(true);
  }, [setVisible]);

  return (
    <form className="data-wrapper">
      <PersonalForm
        {...register("fullName")}
        type={"text"}
        section={"Full Name"}
      />
      <PersonalForm {...register("email")} type={"text"} section={"Email"} />
      <PersonalForm
        {...register("birthday")}
        type={"date"}
        section={"Birthday"}
      />
      <button onClick={onClickCancel}>Cancel</button>
      <button type="submit">Save</button>
    </form>
  );
};

export default PersDataWrapper;
