import React from "react";
import { Link, useMatch, useResolvedPath } from "react-router-dom";
import { logo, basket, account } from "../assets/index.js";
import "./Styles/styles.css";

export const Navbar = () => {
  return (
    <nav>
      <Link to="/">
        <img alt="logo" src={logo} />
      </Link>
      <CustomLink to="/sneakers/men">Men</CustomLink>
      <CustomLink to="/sneakers/women">Women</CustomLink>
      <CustomLink to="/sneakers/kids">Kids</CustomLink>
      <CustomLink to="/sneakers/favorites">Favorites</CustomLink>
      <div>
        <Link to="/auth/account">
          <img alt="account-icon" src={account} />
        </Link>
        <Link to="/auth/basket">
          <img alt="basket-icon" src={basket} />
        </Link>
      </div>
    </nav>
  );
};

const CustomLink = ({ to, children, ...props }) => {
  const resolvedPath = useResolvedPath(to);
  const isActive = useMatch({ path: resolvedPath.pathname, end: true });

  return (
    <li className={isActive ? "active" : ""}>
      <Link to={to} {...props}>
        <p>{children}</p>
      </Link>
    </li>
  );
};
