import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  cards: {
    items: [],
    isLoading: true,
  },
};

const cardsSlice = createSlice({
  name: "cards",
  initialState,
  reducers: {},
});

export const cardsReducer = cardsSlice.reducer;
