import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import "./styles.css";

import { fetchCards } from "../../redux/slices/cards";
import { getPageName } from "../../helpers/PageName";

import Card from "../../components/ui/CardsComponents/card/card.jsx";
import CardSkeleton from "../../components/ui/CardsComponents/cardSkeleton/CardSkeleton";

export const Cards = () => {
  const currantPage = getPageName();

  const dispatch = useDispatch();
  const { cards } = useSelector((state) => state.cards);

  const isCardsLoading = cards.status === "loading";

  useEffect(() => {
    dispatch(fetchCards());
  }, [dispatch]);

  return (
    <div className="humans-container">
      <div className="head">
        <p>{currantPage}`s sneakers</p>
        <div className="form__group field">
          <input type="input" className="form__field" placeholder="Search" />
          <label className="form__label">SEARCH</label>
        </div>
      </div>
      <div className="cards">
        {isCardsLoading ? (
          <CardSkeleton cards={4} />
        ) : (
          cards.items.map((sneaker) => {
            return (
              sneaker.sex === currantPage ||
              ("Unisex" && (
                <Card
                  key={sneaker._id}
                  id={sneaker._id}
                  brand={sneaker.brand}
                  model={sneaker.model}
                  cost={sneaker.cost}
                  images={sneaker.images}
                  color={sneaker.color}
                />
              ))
            );
          })
        )}
      </div>
    </div>
  );
};

export default Cards;
