import React from "react";

import { UserInput } from "../index";

const AddressInput = React.forwardRef(
  ({ error, errorMessage, label, children, ...props }, ref) => {
    return (
      <div className="address-data-input">
        <label style={{ color: error && "red" }}>{label}</label>
        {children ? children : <UserInput ref={ref} type={props.type} />}
        {error && <p>{errorMessage}</p>}
      </div>
    );
  }
);

export default AddressInput;
