import React from "react";
import PhoneInput from "react-phone-input-2";
import "react-phone-input-2/lib/style.css";
import "./styles.css";

import { AddressInput } from "../../forms/index";
import Buttons from "../Buttons/Buttons";
import { useForm } from "react-hook-form";

const UserAdressBlock = ({ setVisible }) => {
  const {
    register,
    handleSubmit,
    formState: { errors, isValid },
  } = useForm({
    defaultValues: {
      lastname: "",
      name: "",
      surname: "",
      postIndex: "",
      region: "",
      street: "",
      city: "",
      phoneNumber: "",
      email: "",
    },
    resolver: yupResolver(editDataSchema),
    mode: "onChange",
  });

  const onSubmit = async () => {};

  return (
    <form onSubmit={handleSubmit(onSubmit)} className="address-data-form">
      <AddressInput {...register("lastname")} label={"Lastname"} />
      <AddressInput {...register("name")} label={"Name"} />
      <AddressInput {...register("surname")} label={"Surname"} />
      <AddressInput {...register("postIndex")} label={"Post index"} />
      <AddressInput {...register("region")} label={"Region"} />
      <AddressInput {...register("street")} label={"Street"} />
      <AddressInput {...register("city")} label={"City/Town"} />
      <AddressInput {...register("phoneNumber")} label={"Phone Number"}>
        <PhoneInput country={"ua"} />
      </AddressInput>
      <AddressInput {...register("email")} label={"Email"} type={"email"} />
      <Buttons disabled={!isValid} setVisible={setVisible} />
    </form>
  );
};

export default UserAdressBlock;
