import React, { useEffect } from "react";
import { Route, Routes, BrowserRouter } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";

import {
  Landing,
  Login,
  NotFound,
  Registration,
  Category,
  Favorites,
  Sneaker,
  Account,
} from "./pages/index.js";

import { fetchAuthMe, selectIsAuth } from "./redux/slices/auth.js";

import { Navbar, Footer } from "./components/routes/index.js";

const App = () => {
  const dispatch = useDispatch();
  const isAuth = useSelector(selectIsAuth);

  console.log(window.location);

  useEffect(() => {
    dispatch(fetchAuthMe());
  }, [dispatch]);

  return (
    <BrowserRouter>
      {window.location.pathname === "/login" || "/registration" ? (
        <Navbar />
      ) : null}
      <Routes>
        <Route path="/" element={<Landing />} />
        <Route path="/user">
          <Route path="account" element={isAuth ? <Account /> : <Login />} />
          <Route path="basket" />
        </Route>
        <Route path="/sneakers">
          <Route path="men" element={<Category />} />
          <Route path="women" element={<Category />} />
          <Route path="kids" element={<Category />} />
          <Route path=":id" element={<Sneaker />} />
          <Route path="favorites" element={<Favorites />} />
        </Route>
        <Route path="login" element={<Login />} />
        <Route path="registartion" element={<Registration />} />
        <Route path="*" element={<NotFound />} />
      </Routes>
      {window.location.pathname === "/login" || ("/registration" && <Footer />)}
    </BrowserRouter>
  );
};

export default App;
