import React, { useEffect, lazy, Suspense } from "react";
import { Route, Routes, BrowserRouter } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";

import {
  Landing,
  NotFound,
  Registration,
  Category,
  Favorites,
  Sneaker,
  Account,
} from "./pages/index.js";

import { fetchAuthMe, selectIsAuth } from "./redux/slices/auth.js";

import { Navbar, Footer } from "./components/routes/index.js";

const Login = lazy(() => import("./pages/Authentication/Login.jsx"));

const App = () => {
  const dispatch = useDispatch();
  const isAuth = useSelector(selectIsAuth);

  useEffect(() => {
    dispatch(fetchAuthMe());
  }, [dispatch]);

  return (
    <BrowserRouter>
      <Navbar />
      <Routes>
        <Route path="/" element={<Landing />} />
        <Route path="/user">
          <Suspense
            fallback={
              <div>
                <Route
                  path="account"
                  element={isAuth ? <Account /> : <Login />}
                />
              </div>
            }
          ></Suspense>
          <Route path="basket" />
        </Route>
        <Route path="/sneakers">
          <Route path="men" element={<Category />} />
          <Route path="women" element={<Category />} />
          <Route path="kids" element={<Category />} />
          <Route path=":id" element={<Sneaker />} />
          <Route path="favorites" element={<Favorites />} />
        </Route>
        <Route path="login" element={<Login />} />
        <Route path="registartion" element={<Registration />} />
        <Route path="*" element={<NotFound />} />
      </Routes>
      <Footer />
    </BrowserRouter>
  );
};

export default App;
