export const reducer = (state, action) => {
  switch (action.type) {
    case "data": {
      return (state.data = ture);
    }
    case "address": {
      return !state.address;
    }
    case "password": {
      return !state.password;
    }
  }

  throw Error(`Unknowm action: ${action.type}`);
};
