import React from "react";

import "./styles.css";

const Input = React.forwardRef(({ error, errorMessage, ...props }, ref) => {
  return (
    <>
      <input className={error ? "input-error" : "input"} ref={ref} {...props} />
      {error && <p>{errorMessage}</p>}
    </>
  );
});

export default Input;
