import React, { useCallback } from "react";
import PersonalForm from "./PersonalForm";
import { useForm } from "react-hook-form";
import { useSelector } from "react-redux";
import { yupResolver } from "@hookform/resolvers/yup";
import { editDataSchema } from "../../../schemas";
import "./styles.css";

const PersDataWrapper = ({ setVisible }) => {
  const { fullName, email } = useSelector((state) => state.auth.data);

  const {
    register,
    handleSubmit,
    formState: { errors, isValid },
  } = useForm({
    defaultValues: {
      fullName: fullName,
      email: email,
      birthday: "2023-01-01",
    },
    resolver: yupResolver(editDataSchema),
    mode: "onChange",
  });

  const onClickCancel = useCallback(() => {
    setVisible(false);
  }, [setVisible]);

  const onSubmit = () => {
    setVisible(true);
  };

  return (
    <form onSubmit={handleSubmit(onSubmit)} className="data-wrapper">
      <PersonalForm
        {...register("fullName")}
        type={"text"}
        section={"Full Name"}
        error={Boolean(errors.fullName?.message)}
        errorMessage={errors.fullName?.message}
      />
      <PersonalForm
        error={Boolean(errors.email?.message)}
        errorMessage={errors.email?.message}
        {...register("email")}
        type={"text"}
        section={"Email"}
      />
      <PersonalForm
        {...register("birthday")}
        type={"date"}
        section={"Birthday"}
        error={Boolean(errors.birthday?.message)}
        errorMessage={errors.birthday?.message}
      />
      <div className="buttons">
        <button onClick={onClickCancel}>Cancel</button>
        <button disabled={!isValid} type="submit">
          Save
        </button>
      </div>
    </form>
  );
};

export default PersDataWrapper;
