import fromUnixTime from "date-fns/fromUnixTime";
export const getDate = (date) => {
  const day = fromUnixTime(date).getDay();
  const month = fromUnixTime(date).getMonth();
  const year = fromUnixTime(date).getFullYear();
  return day + "." + month + "." + year;
};
