import React from "react";

import { UserInput } from "../index";

const AddressInput = React.forwardRef(
  ({ register, error, errorMessage, label, children, ...props }, ref) => {
    return (
      <div className="address-data-input">
        <label style={{ color: error && "red" }}>{label}</label>
        {children ? (
          children
        ) : (
          <UserInput
            {...register}
            error={error}
            errorMessage={errorMessage}
            ref={ref}
            type={props.type}
          />
        )}
      </div>
    );
  }
);

export default AddressInput;
