import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";

export const fetchFavorites = createAsyncThunk(
  "/cards/fetchGavorites",
  async (favorites) => {
    const { data } = await axios.get("/cards");

    let filteredData = [];
    for (let i = 0; i < favorites.length; i++) {
      filteredData.push(data.find((fav) => fav._id === favorites[i]));
    }
    return filteredData;
  }
);

const initialState = {
  favorites: [],
};

const favoritesSlice = createSlice({
  name: "favorites",
  initialState,
  reducers: {
    addToFavorites(state, action) {
      state.favorites.push(action.payload);
    },
  },
  extraReducers: {
    [fetchFavorites.pending]: (state) => {
      state.cards.items = [];
      state.cards.status = "loading";
    },
    [fetchFavorites.fulfilled]: (state, action) => {
      state.cards.items = action.payload;
      state.cards.status = "loaded";
    },
    [fetchFavorites.rejected]: (state) => {
      state.cards.items = [];
      state.cards.status = "error";
    },
  },
});

export const { addToFavorites } = favoritesSlice.actions;

export const favoritesReducer = favoritesSlice.reducer;
