import React, { useState } from "react";
import { useSelector } from "react-redux";
import "./styles.css";

import { InfoBlock } from "../../components/ui/index";
import { SideNav } from "../../components/routes/index";
import {
  shipStyles,
  shippingStyles,
  userDataStyles,
  passwordStyles,
  passwordData,
  shipData,
} from "../../helpers/AccountData";
import PersDataWrapper from "../../components/forms/PersonalData/PersDataWrapper";

const Account = () => {
  const [showData, setShowData] = useState(false);
  const [showAddress, setShowAddress] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const { fullName, email, avatarUrl } = useSelector(
    (state) => state.auth.data
  );

  const personalData = [fullName, email];
  const addressData = [];

  return (
    <div className="account-container">
      <SideNav avatar={avatarUrl} />
      <div className="middle">
        {!showData ? (
          <InfoBlock
            setVisible={setShowData}
            styles={userDataStyles}
            data={personalData}
            section={"Presonal Data"}
          />
        ) : (
          <PersDataWrapper setVisible={setShowData} />
        )}
        {!showAddress ? (
          <InfoBlock
            setVisible={setShowAddress}
            section={"My shipping addresses"}
            data={addressData}
            styles={shippingStyles}
          >
            <button className="new-address">Add new address</button>
          </InfoBlock>
        ) : (
          <h1>Address</h1>
        )}
        {!showPassword ? (
          <InfoBlock
            setVisible={setShowPassword}
            section={"Change Password"}
            data={passwordData}
            styles={passwordStyles}
          />
        ) : (
          <h1>Password</h1>
        )}
      </div>
      <div className="right">
        <InfoBlock
          data={shipData}
          styles={shipStyles}
          section={"Default Shipping"}
        />
        <InfoBlock
          data={shipData}
          styles={shipStyles}
          section={"Default Shipping Method"}
        />
      </div>
    </div>
  );
};

export default Account;
