import React from "react";

import "./styles.css";

const Input = ({ ...props }) => {
  return (
    <div className="form__group">
      <input {...props} className="form__field" />
      <label className="form__label">{props.label}</label>
      {props.error && <p>{props.errorMessage}</p>}
    </div>
  );
};

export default Input;
