import React, { useReducer, useState } from "react";
import { useSelector } from "react-redux";
import "./styles.css";

import { InfoBlock } from "../../components/ui/index";
import { SideNav } from "../../components/routes/index";
import {
  shipStyles,
  shippingStyles,
  userDataStyles,
  passwordStyles,
  passwordData,
  shipData,
} from "../../helpers/AccountData";
import PersDataWrapper from "../../components/forms/PersonalData/PersDataWrapper";
import { reducer } from "../../helpers/AccountReducer";

const Account = () => {
  const [state, dispatch] = useReducer(reducer, {
    data: false,
    address: false,
    password: false,
  });
  const { fullName, email, avatarUrl } = useSelector(
    (state) => state.auth.data
  );

  const personalData = [fullName, email];
  const addressData = [];

  return (
    <div className="account-container">
      <SideNav avatar={avatarUrl} />
      <div className="middle">
        {!state.data ? (
          <InfoBlock
            setVisible={dispatch({ type: "data" })}
            styles={userDataStyles}
            data={personalData}
            section={"Presonal Data"}
          />
        ) : (
          <PersDataWrapper setVisible={setShowData} />
        )}
        {!state.address ? (
          <InfoBlock
            setVisible={dispatch({ type: "address" })}
            section={"My shipping addresses"}
            data={addressData}
            styles={shippingStyles}
          >
            <button className="new-address">Add new address</button>
          </InfoBlock>
        ) : (
          <h1>Address</h1>
        )}
        {!state.password ? (
          <InfoBlock
            setVisible={dispatch({ type: "password" })}
            section={"Change Password"}
            data={passwordData}
            styles={passwordStyles}
          />
        ) : (
          <h1>Password</h1>
        )}
      </div>
      <div className="right">
        <InfoBlock
          data={shipData}
          styles={shipStyles}
          section={"Default Shipping"}
        />
        <InfoBlock
          data={shipData}
          styles={shipStyles}
          section={"Default Shipping Method"}
        />
      </div>
    </div>
  );
};

export default Account;
