export const togglePasswordReducer = (state, action) => {
  switch (action.type) {
    case "old": {
      return { ...state, data: !state.old };
    }
    case "new": {
      return { ...state, address: !state.new };
    }

    default:
      return console.log(Error(`Unknowm action: ${action.type}`));
  }
};
