import React, { useState } from "react";
import { useSelector } from "react-redux";
import { useLocation } from "react-router-dom";

import { selectIsAuth } from "../../redux/slices/auth.js";

import { basket, account, logo } from "../../assets/index.js";
import { CustomLink } from "../ui/index.js";

import "./styles.css";

const Navbar = () => {
  const [hide, setHide] = useState();
  const { pathname } = useLocation();
  const isAuth = useSelector(selectIsAuth);
  // console.log(path);

  {
    pathname === "/login" ||
      ("/registration" && (
        <nav>
          <CustomLink to="/">
            <img className="logo" alt="logo" src={logo} />
          </CustomLink>
          <CustomLink to="/sneakers/men">
            <p>Men</p>
          </CustomLink>
          <CustomLink to="/sneakers/women">
            <p>Women</p>
          </CustomLink>
          <CustomLink to="/sneakers/kids">
            <p>Kids</p>
          </CustomLink>
          <CustomLink to="/sneakers/favorites">
            <p>Favorites</p>
          </CustomLink>
          <CustomLink
            className="icons"
            to={isAuth ? "/user/account" : "/login"}
          >
            <img className="icon" alt="account-icon" src={account} />
          </CustomLink>
          <CustomLink
            className="icons2"
            to={isAuth ? "/user/basket" : "/login"}
          >
            <img className="icon" alt="basket-icon" src={basket} />
          </CustomLink>
        </nav>
      ));
  }
};

export default Navbar;
