import React from "react";
import { Link, useMatch, useResolvedPath } from "react-router-dom";
import { logo, basket, account } from "../assets/index.js";
import "./Styles/styles.css";

export const Navbar = () => {
  return (
    <nav className="navbar">
      <Link className="logo-nav" to="/">
        <img alt="logo" className="logo" src={logo} />
      </Link>
      <Link to="/sneakers/men" className="nav">
        <p>Men</p>
      </Link>
      <Link to="/sneakers/women" className="nav">
        <p>Women</p>
      </Link>
      <Link to="/sneakers/kids" className="nav">
        <p>Kids</p>
      </Link>
      <Link to="/sneakers/favorites" className="nav">
        <p>Favorites</p>
      </Link>
      <div className="icons-nav">
        <Link to="/auth/account" className="icon-nav">
          <img alt="account-icon" src={account} className="icon" />
        </Link>
        <Link to="/auth/basket" className="icon-nav">
          <img alt="basket-icon" src={basket} className="icon" />
        </Link>
      </div>
    </nav>
  );
};

const CustomLink = ({ to, children, ...props }) => {
  const resolvedPath = useResolvedPath(to);
  const isActive = useMatch({ path: resolvedPath.pathname, end: true });

  return (
    <li className={isActive ? "active" : ""}>
      <Link to={to} {...props}>
        {children}
      </Link>
    </li>
  );
};
