import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import "./styles.css";

import { fetchCardData } from "../../redux/slices/cardData";

import { Carousel, CardInfo } from "../../components/ui/CardsComponents/index";
import BackLine from "../../components/ui/backLine/backLine.jsx";

const CardPage = () => {
  const { id } = useParams();
  const { card } = useSelector((state) => state.card);
  const dispatch = useDispatch();

  const { brand, model, cost, materials, description } = card.data;

  useEffect(() => {
    dispatch(fetchCardData(id));
  }, [dispatch, id]);

  return (
    <div className="card-page">
      <BackLine />
      <div className="sneaker-content">
        <Carousel />
        <CardInfo
          brand={brand}
          model={model}
          cost={cost}
          description={description}
          materials={materials}
        />
      </div>
    </div>
  );
};

export default CardPage;
