import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import "./styles.css";

import { fetchCardData } from "../../redux/slices/cardData";

import { Carousel, CardInfo } from "../../components/ui/CardsComponents/index";
import BackLine from "../../components/ui/backLine/backLine.jsx";
import Spinner from "../../components/ui/CardsComponents/spinner/Spinner";

const CardPage = () => {
  const { id } = useParams();
  const { card } = useSelector((state) => state.card);
  const dispatch = useDispatch();

  const isCardLoading = card.status === "loading";

  const { images, brand, model, cost, materials, description } = card.data;

  console.log(images);

  useEffect(() => {
    dispatch(fetchCardData(id));
    console.log(images);
  }, [dispatch, id]);

  return (
    <div className="card-page">
      <BackLine />
      <div className="sneaker-content">
        {isCardLoading ? (
          <Spinner />
        ) : (
          <>
            <Carousel images={images} />
            <CardInfo
              brand={brand}
              model={model}
              cost={cost}
              description={description}
              materials={materials}
            />
            <Spinner />
          </>
        )}
      </div>
    </div>
  );
};

export default CardPage;
