import React from "react";
import { useDispatch, useSelector } from "react-redux";

import { logout } from "../../redux/slices/auth";

import { CustomLink } from "../../components/ui/index";

import "./styles.css";

const Account = () => {
  const { fullName, email, avatarUrl } = useSelector(
    (state) => state.auth.data
  );

  const dispatch = useDispatch();

  const onClickLogout = () => {
    if (window.confirm("Are you sure you want to log out?")) {
      dispatch(logout());
      window.localStorage.removeItem("token");
    }
  };

  return (
    <div className="account-container">
      <div className="left">
        <div className="links">
          <CustomLink className="account">My Account</CustomLink>
          <CustomLink className="orders">My Orders</CustomLink>
          <button className="logout" onClick={onClickLogout}>
            Logout
          </button>
        </div>
      </div>
      <div className="middle"></div>
      <div className="right"></div>
    </div>
  );
};

export default Account;
