import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { useForm } from "react-hook-form";
import { Navigate } from "react-router-dom";

import { selectIsAuth } from "../../redux/slices/auth";

import { AuthInput } from "../../components/forms/index";

export const Registartion = () => {
  const isAuth = useSelector(selectIsAuth);
  const dispatch = useDispatch();

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    defaultValues: {
      fullName: "",
      email: "",
      password: "",
    },
    mode: "onChange",
  });

  const onSubmit = async (values) => {
    const data = await dispatch(fetchAuth(values));

    if (!data.payload) {
      return alert("Authenticate failed");
    }

    if ("token" in data.payload) {
      window.localStorage.setItem("token", data.payload.token);
    }
  };

  if (isAuth) {
    return <Navigate to={"/"} />;
  }

  return (
    <div className="reg-container">
      <AuthInput label={"AVATAR URL"} type="text" placeholder={"AVATAR URL"} />
      <AuthInput label={"FULL NAME"} type="text" placeholder={"FULL NAME"} />
      <AuthInput label={"EMAIL"} type="email" placeholder={"EMAIL"} />
      <AuthInput label={"PASSWORD"} type="password" placeholder={"PASSWORD"} />
    </div>
  );
};

export default Registartion;
