import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import "./styles.css";

import axios from "../../axios";
import { selectIsAuth } from "../../redux/slices/auth";

import { CardSkeleton, CustomLink } from "../../components/ui/index";
import { Card } from "../../components/ui/CardsComponents/index";

import { icon_back, smiling_sneaker } from "../../assets/index";

const Favorites = () => {
  const isAuth = useSelector(selectIsAuth);
  const userData = useSelector((state) => state.auth.data);
  const [cards, setCards] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  console.log(cards.length);

  useEffect(() => {
    if (isAuth) {
      axios.get(`/favorites:${userData?._id}`).then((res) => {
        setCards(res.data.cards);
        setIsLoading(false);
      });
    }
  }, []);

  return (
    <div className="favorites-contaner">
      {isAuth ? (
        isLoading ? (
          <CardSkeleton cards={4} />
        ) : cards.length === 0 ? (
          <div className="empty">
            <h1>Something is missing here</h1>
            <CustomLink to={"/sneakers/men"}>View products</CustomLink>
            <img src={smiling_sneaker} />
          </div>
        ) : (
          cards.map((card) => <Card key={card._id} card={card} />)
        )
      ) : (
        <div className="login">
          <CustomLink to={"/login"}>At first you need to login</CustomLink>
          <img src={icon_back} />
        </div>
      )}
    </div>
  );
};

export default Favorites;
